  },
  {
    status: false,
    comment: "I liked the content, it helped me to solve my problem",
  },
];

function WebSiteDetail(props) {

  function handleBackBtnClick(event) {
    props.onBack(undefined);
  }

  return (
    <div className="col-7 mx-3">
      <button
        className="btn btn-outline-primary mb-2"
        onClick={handleBackBtnClick}
      >
        <img src={backBtn} />
        Back
      </button>
      <button
        className="btn btn-outline-dark mx-1"
        data-toggle="modal"
        data-target="#AddRecommendationModal"
      >
        New Recommendation
      </button>
      <WebsiteCard webSiteData={props.webSite} onSelect={() => {}} />
      <hr />
      <WebSiteComments comments={DUMMY_COMMENTS} />
    </div>
  );
}

export default WebSiteDetail;
