import SideBarPopularWebsites from "./SideBarPopularWebsites";
import SideBarProfile from "./SideBarProfil/SideBarProfile";
import WebSiteCards from "./WebSites/WebSiteCards";
import WebSiteDetail from "./WebSiteDetail";
import { useState } from "react";
import ReactDOM from "react-dom";
import AddRecommendationModal from "../AddRecommendationModal";

function MainContent(props) {
  const [webSiteSelected, setWebSiteSelected] = useState();

  const addRecommendationPortal = document.getElementById("add-rec-modal");

  function handleWebSiteSelectedChange(website) {
    setWebSiteSelected(website);
  }

  return (
    <div className="container mt-3 col">
      {ReactDOM.createPortal(
        <AddRecommendationModal />,
        addRecommendationPortal
      )}
      <div className="d-flex justify-content-end">
        {webSiteSelected && <button
          className="btn btn-outline-dark mx-1"
          data-toggle="modal"
          data-target="#AddRecommendationModal"
        >
          New Recommendation
        </button>}
        <button className="btn btn-outline-dark mx-1">
          My Recommendations
        </button>
      </div>
      <hr class="p-0" />
      <div className="d-flex justify-content-center mt-2">
        <SideBarProfile />
        {webSiteSelected !== undefined ? (
          <WebSiteDetail
            webSite={webSiteSelected}
            onBack={handleWebSiteSelectedChange}
          />
        ) : (
          <WebSiteCards onSelectCard={handleWebSiteSelectedChange} />
        )}
        <SideBarPopularWebsites />
      </div>
    </div>
  );
}

export default MainContent;
