    //     return response.json();
    //   })
    //   .then((data) => {
    //     console.log(data)
    //     props.onSearch(data);
    //   })
    //   .catch((err) => {
    //     alert(err.message);
    //   });
  }

  return (
    <form
      class="d-flex flex-grow w-50 ms-5 me-5"
      onSubmit={handleSearchFormSubmit}
    >
      <input
        class="form-control me-2"
        type="search"
        placeholder="Search"
        aria-label="Search"
        ref={searchTextRef}
      />
      <button class="btn btn-outline-success" type="submit">
        Search
      </button>
    </form>
  );
}

export default SearchBar;
