import "./App.css";
import React, { useEffect, useState } from "react";
import Header from "./components/Header/Header";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer";
import SigninModal from "./components/SigninModal";
import SignupModal from "./components/SignupModal";
import AddRecommendationModal from "./components/AddRecommendationModal";
import ReactDOM from "react-dom";
import reactDom from "react-dom";
import { AuthContextProvider } from "./store/auth-context/AuthContext";
import { Redirect, Route } from "react-router-dom";

import Login from "./pages/Login/Login";

function App() {
  const [searchResults, setSearchResults] = useState();

  function handleSearchResults(searchResults) {
    setSearchResults(searchResults);
  }

  return (
    <AuthContextProvider>
      <Header onSearch={handleSearchResults} />
      <Route exact path="/login">
        <Login />
      </Route>
      <Route exact path="/home">
        <MainContent searchResults={searchResults} />
      </Route>
      <Route path="/">
        <Redirect to="/home" />
      </Route>
      <Footer />
    </AuthContextProvider>
  );
}

export default App;
