import { useEffect, useContext, useState } from "react";
import AuthContext from "../../store/auth-context/AuthContext";

import WebsiteCard from "./WebSites/WebsiteCard";
import WebSiteCards from "./WebSites/WebSiteCards";
import WebSiteComment from "./WebSites/WebSiteComment";
import WebSiteComments from "./WebSites/WebSiteComments";

function MyRecommendations(props) {
  const auth = useContext(AuthContext);
  const [recommendations, setRecommendations] = useState();

  useEffect(() => {
    fetch(`http://localhost:8080/api/users/${auth.username}/recs`, {
      headers: {
        Authorization: `Bearer ${auth.token}`,
      },
    })
      .then((response) => {
        if (!response.ok) {
          alert("Failed while retrieving recommendations");
        }
        return response.json();
      })
      .then((data) => {
        console.log(data);
        setRecommendations(data);
      });
  }, []);

  return (
    <>
      {recommendations &&
        recommendations.map((rec) => {

          <>
          <p>inside map</p>
            <div className="col-7 mx-3">
              <hr className="gy-0 p-0" />
              {recommendations.map((rec) => (
                <>
                  <WebsiteCard
                    key={rec.recommendedWebPage.id}
                    onSelect={props.onSelectCard}
                    webSiteData={rec.recommendedWebPage}
                  />
                  <WebSiteComment key={rec.id} commentData={rec} />
                </>
              ))}
            </div>
            <hr />
          </>;
        })}
    </>
  );
}

export default MyRecommendations;
