import "./App.css";
import React, { useContext, useEffect, useState } from "react";
import Header from "./components/Header/Header";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer";
import AuthContext, {
  AuthContextProvider,
} from "./store/auth-context/AuthContext";
import { Redirect, Route } from "react-router-dom";

import Login from "./pages/Login/Login";
import Signup from "./pages/Signup/Signup";
import Profile from "./pages/Profile/Profile";

function App() {
  const [searchResults, setSearchResults] = useState();
  const auth = useContext(AuthContext);

  function handleSearchResults(searchResults) {
    setSearchResults(searchResults);
  }

  return (
    <AuthContextProvider>
      <Header onSearch={handleSearchResults} />
      <Route exact path="/login">
        {auth.isAuthenticated === true ? <Redirect to="/home" /> : <Login />}
      </Route>
      <Route exact path="/signup">
        {auth.isAuthenticated ? <Redirect to="/home" /> : <Signup />}
      </Route>
      <Route exact path="/home">
        <MainContent searchResults={searchResults} />
      </Route>
      <Route exact path="/">
        <Redirect to="/home" />
      </Route>
      <Route exact path="/profile">
         <Profile />
      </Route>
      <Footer />
    </AuthContextProvider>
  );
}

export default App;
