import './WebsiteCard.css';

function WebsiteCard(props) {

  function handleCardClick(event) {
    props.onSelect(props.webSiteData)
  }

  const classes = "card shadow mx-auto rounded mb-3";
  if (props.clikable === false) {
    classes = classes + " not-active";
  } 


  return (
    <div class="card shadow mx-auto rounded mb-3 not-clikable" onClick={handleCardClick}>
      <div class="row g-0">
        <div class="col">
          <div class="card-body">
            <h5 class="card-title">
              {props.webSiteData.title} 
            </h5>
            <p class="card-text">
              {props.webSiteData.description}
            </p>
            <p class="card-text">
              <small class="text-muted">Last updated {props.webSiteData.lastUpdated} mins ago</small>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default WebsiteCard;
