import "./App.css";
import React, { useEffect } from "react";
import Header from "./components/Header/Header";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer";
import SigninModal from "./components/SigninModal";
import SignupModal from "./components/SignupModal";
import AddRecommendationModal from "./components/AddRecommendationModal";
import ReactDOM from "react-dom";
import reactDom from "react-dom";

function App() {
  const signInModalPortal = document.getElementById("signin-modal");
  const signupModalPortal = document.getElementById("signup-modal");

  return (
    <React.Fragment>
      {ReactDOM.createPortal(<SigninModal />, signInModalPortal)}
      {reactDom.createPortal(<SignupModal />, signupModalPortal)}
      <Header />
      <MainContent />
      <Footer />
    </React.Fragment>
  );
}

export default App;
