import { createContext, useEffect, useState } from "react";

export default AuthContext = createContext({
  token: "",
  isAuthenticated: false,
  login: () => {},
  logout: () => {},
});

export function AuthContextProvider(props) {
  const [authState, setAuthState] = useState({
    token: "",
    isAuthenticated: false,
  });

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setAuthState({ token: storedToken, isAuthenticated: true });
    }
  }, []);

  function loginHandler(authToken) {
    setAuthState({ token: authToken, isAuthenticated: true });
    localStorage.setItem("token", authToken);
  }

  function logoutHandler() {
    setAuthState({ token: "", isAuthenticated: false });
    localStorage.removeItem("token");
  }

  const authContext = {
    token: authState.token,
    isAuthenticated: authState.isAuthenticated,
    login: loginHandler,
    logout: logoutHandler,
  };

  return (
    <AuthContext.Provider value={authContext}>
      {props.children}
    </AuthContext.Provider>
  );
}
