import { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import WebsiteCard from "./WebSites/WebsiteCard";
import SortPanel from "./WebSites/SortPanel";

function SearchResults(props) {
  const [searchResults, setSearchResults] = useState([]);

  const location = useLocation();
  const history = useHistory();
  const searchParams = new URLSearchParams(location);
  const searchText = searchParams.get("query");

  if (!searchText) {
    history.push("/home");
  }


  useEffect(() => {
    fetch("http://localhost:8080/api/search?query=" + searchText)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Error: Couldn't perform search operation");
        }

        return response.json();
      })
      .then((data) => {
        console.log(data);
        setSearchResults(data);
      })
      .catch((err) => {
        alert(err.message);
      }),
      [];
  });


  return (
    <div className="col-7 mx-3">
      <SortPanel />
      <hr className="gy-0 p-0" />
      {searchResults &&
        searchResults.map((site) => (
          <WebsiteCard
            key={site.id}
            onSelect={props.onSelectCard}
            webSiteData={site}
          />
        ))}
    </div>
  );
}

export default SearchResults;
