import SortPanel from "./SortPanel";
import WebsiteCard from "./WebsiteCard";

import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function sort(arr, by) {
  for (let i = 0; i < arr.length; i++) {
    let indexOfMax = i;
    for (let j = i + 1; j < arr.length; j++) {
      if (by === "likes") {
        if (arr[j].numberOfLikes > arr[indexOfMax].numberOfLikes) {
          indexOfMax = j;
        }
      } else if (by === "dislikes") {
        if (arr[j].numberOfDislikes > arr[indexOfMax].numberOfDislikes) {
          indexOfMax = j;
        }
      }
    }

    const maxValue = arr[indexOfMax];
    arr[indexOfMax] = arr[i];
    arr[i] = maxValue;
  }
}

function WebSiteCards(props) {
  const [webSites, setWebSites] = useState();
  const location = useLocation();

  const searchParams = new URLSearchParams(location.search);
  const sortByParam = searchParams.get("sortBy");

  useEffect(() => {
    if (!props.websites) {
      fetch("http://localhost:8080/api/webpages")
        .then((response) => {
          if (!response.ok) {
            throw new Error("Error: Failed To Fetch");
          }
          return response.json();
        })
        .then((data) => {
          if (sortByParam.toLowerCase() === "liked") {
            sort(webSites, "likes");
          } else if (sortByParam.toLowerCase() === "disliked") {
            sort(webSites, "dislikes");
          }
          setWebSites(data);
        })
        .catch((err) => alert(err.message));
    } else {
      setWebSites(props.websites);
    }
  }, [props.websites, sortByParam]);

  return (
    <div className="col-7 mx-3">
      <SortPanel />
      <hr className="gy-0 p-0" />
      {webSites &&
        webSites.map((site) => (
          <WebsiteCard
            key={site.id}
            onSelect={props.onSelectCard}
            webSiteData={site}
          />
        ))}
    </div>
  );
}

export default WebSiteCards;
