import { useContext, useEffect, useState } from "react";
import AuthContext from "../../../store/auth-context/AuthContext";

function SideBarProfile(props) {
  const authContext = useContext(AuthContext);
  const [authorizedUser, setAuthorizedUser] = useState();

  useEffect(async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/users/${authContext.username}`,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authContext.token}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Authentication failed");
      }
      const data = await response.json();
      await setAuthorizedUser(data);
    } catch (err) {
      alert(err.message);
    }
  }, [authContext]);

  return (
    <div class="col-2 h-100 me-3">
      <div class="row mb-3">
        <div class="card bg-light">
          <div class="card-body text-center">
            <p>
              <img
                class="img-fluid"
                src="https://sunlimetech.com/portfolio/boot4menu/assets/imgs/team/img_01.png"
                alt="card image"
              />
            </p>
            <h4 class="card-title">{`${authorizedUser.firstName} ${authorizedUser.lastName}`}</h4>
            <p class="card-text">
              {authorizedUser.occupation}
              <br />
              {authorizedUser.eduLevel}
            </p>
            <a
              href="https://www.fiverr.com/share/qb8D02"
              class="btn btn-primary btn-sm"
            >
              Go to Profile
            </a>
          </div>
        </div>
      </div>
      <div class="row mb-3">
        <div class="card bg-light">
          <div class="card-body text-center">
            <h4 class="card-title">Interests</h4>
            <p className="card-text">
              IInterests InterestInterests InterestInterests InterestInterests
              InterestInterests InterestInterests Interestnterests Interest
            </p>
          </div>
        </div>
      </div>
      <div class="row mb-3">
        <div class="card bg-light">
          <div class="card-body text-center">
            <h4 class="card-title">Your Last Searches</h4>
            <p className="card-text">
              IInterests InterestInterests InterestInterests InterestInterests
              InterestInterests InterestInterests Interestnterests Interest
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SideBarProfile;
