import React, { useState } from "react";

function AddRecommendationModal(props) {
  const [formData, setFormData] = useState({
    liked: false,
    url: "",
    comment: "",
  });

  function handleLikedOrDisliked(event) {
    console.log(event.target.value);
    if (event.target.name === "liked") {
      setFormData((prevState) => {
        return { ...prevState, liked: true };
      });
    } 
    else if (event.target.name === "disliked") {
      setFormData((prevState) => {
        return { ...prevState, liked: true };
      });
    }
  }

  function handleUrlChange(event) {
    console.log(event.target.value);
    setFormData((prevState) => {
      return { ...prevState, url: event.target.value };
    });
  }

  function handleCommentChange(event) {
    console.log(event.target.value);
    setFormData((prevState) => {
      return { ...prevState, comment: event.target.value };
    });
  }

  function handleFormSubmit(event) {
    event.preventDefault();
    console.log(formData);
    setFormData({
      liked: false,
      url: "",
      comment: "",
    });
  }

  return (
    <div id="AddRecommendationModal" className="modal fade">
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
          <div className="modal-header justify-content-center">
            <h1 className="modal-title">New Recommendation</h1>
          </div>
          <div className="modal-body">
            <form role="form" method="POST" onSubmit={handleFormSubmit}>
              <input type="hidden" name="_token" />
              <div className="form-group">
                <div>
                  <div className="radio">
                    <label>
                      <input
                        type="radio"
                        name="remember"
                        onChange={handleLikedOrDisliked}
                      />{" "}
                      Liked
                    </label>
                  </div>
                  <div className="radio">
                    <label>
                      <input
                        type="radio"
                        name="remember"
                        onChange={handleLikedOrDisliked}
                      />{" "}
                      Disliked
                    </label>
                  </div>
                </div>
              </div>
              <div className="form-group">
                <label className="control-label">Url</label>
                <div>
                  <input
                    type="url"
                    className="form-control input-lg"
                    name="url"
                    value={formData.url}
                    onChange={handleUrlChange}
                  />
                </div>
              </div>
              <div className="form-group">
                <label>Your Comment</label>
                <input
                  type="text"
                  className="form-control input-lg"
                  name="comment"
                  value={formData.comment}
                  onChange={handleCommentChange}
                />
              </div>
              <div className="form-group">
                <div>
                  <button type="submit" className="btn btn-success">
                    POST
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddRecommendationModal;
