import React, { useContext, useRef } from "react";
import AuthContext from "../store/auth-context/AuthContext";

function SigninModal(props) {
  const authContext = useContext(AuthContext);

  const usernameRef = useRef();
  const passwordRef = useRef();
  const modalDialog = useRef();

  function handleloginFormSubmit(event) {
    event.preventDefault();

    const username = usernameRef.current.value;
    const password = passwordRef.current.value;

    fetch("http://localhost:8080/api/auth/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("UnSuccessfull");
        }

        return response.json();
      })
      .then((data) => {
        authContext.login(data.token, data.username);
        window.$("#ModalLoginForm").modal("hide");
      })
      .catch((err) => alert(err.message));
  }

  return (
    <div id="ModalLoginForm" className="modal fade" ref={modalDialog}>
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title">Login</h1>
          </div>
          <div className="modal-body">
            <form onSubmit={handleloginFormSubmit}>
              <input type="hidden" name="_token" />
              <div className="form-group">
                <label className="control-label">E-Mail Address</label>
                <div>
                  <input
                    type="text"
                    className="form-control input-lg"
                    name="username"
                    ref={usernameRef}
                  />
                </div>
              </div>
              <div className="form-group">
                <label className="control-label">Password</label>
                <div>
                  <input
                    type="password"
                    className="form-control input-lg"
                    name="password"
                    ref={passwordRef}
                  />
                </div>
              </div>
              <div className="form-group">
                <div>
                  <div className="checkbox">
                    <label>
                      <input type="checkbox" name="remember" /> Remember Me
                    </label>
                  </div>
                </div>
              </div>
              <div className="form-group">
                <div>
                  <button type="submit" className="btn btn-success">
                    Login
                  </button>
                  <a className="btn btn-link" href="#">
                    Forgot Your Password?
                  </a>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SigninModal;
