import "./App.css";
import React, { useEffect, useState } from "react";
import Header from "./components/Header/Header";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer";
import SigninModal from "./components/SigninModal";
import SignupModal from "./components/SignupModal";
import AddRecommendationModal from "./components/AddRecommendationModal";
import ReactDOM from "react-dom";
import reactDom from "react-dom";
import { AuthContextProvider } from "./store/auth-context/AuthContext";
import { Route } from "react-router-dom";

import Login from "./pages/Login/Login";

function App() {
  const [searchResults, setSearchResults] = useState();

  function handleSearchResults(searchResults) {
    setSearchResults(searchResults);
  }

  const signInModalPortal = document.getElementById("signin-modal");
  const signupModalPortal = document.getElementById("signup-modal");

  return (
    <AuthContextProvider>
      <Route exact path="/login">
        <Login />
      </Route>
      <Route exact path="/">
        {ReactDOM.createPortal(<SigninModal />, signInModalPortal)}
        {reactDom.createPortal(<SignupModal />, signupModalPortal)}
        <Header onSearch={handleSearchResults} />
        <MainContent searchResults={searchResults} />
        <Footer />
      </Route>
    </AuthContextProvider>
  );
}

export default App;
