import SideBarPopularWebsites from "./SideBarPopularWebsites";
import SideBarProfile from "./SideBarProfil/SideBarProfile";
import WebSiteComments from "./WebSiteComments";
import WebSiteCards from "./WebSites/WebSiteCards";
import WebSiteDetail from "./WebSiteDetail";
import { useState } from "react";

function MainContent(props) {
  const [contentSwitch, setContentSwitch] = useState(0);

  function handleContentSwitch() {
    setContentSwitch(1);
  }

  return (
    <div className="container mt-3 col">
      <div className="d-flex justify-content-end">
        <button className="btn btn-outline-dark mx-1">
          Add New Recommendation
        </button>
        <button className="btn btn-outline-dark mx-1">
          My Recommendations
        </button>
      </div>
      <hr class="p-0" />
      <div className="d-flex justify-content-center mt-2">
        <SideBarProfile />
        {contentSwitch === 1 && <WebSiteDetail />}
        {contentSwitch === 0 && <WebSiteCards onSelectCard={handleContentSwitch} />}
        <SideBarPopularWebsites />
      </div>
    </div>
  );
}

export default MainContent;
